########################################################################################################################
### main
########################################################################################################################

.text

.globl main


main: 
     addi    $sp, $sp, -4    # Make space on stack
    sw      $ra, 0($sp)     # Save return address
         
    # Start test 1 
    ############################################################
    la      $a0, asize1     # 1st parameter: address of asize1[0]
    la      $a1, frame1     # 2nd parameter: address of frame1[0]
    la      $a2, window1    # 3rd parameter: address of window1[0] 
   
    jal     vbsme           # call function
   jal     print_result    # print results to console
    
    ############################################################
    # End of test 1   


    lw      $ra, 0($sp)         # Restore return address
    addi    $sp, $sp, 4         # Restore stack pointer
    j       end_program         # Return


end_program:                    # remain in infinite loop
	j end_program
################### Print Result ####################################
print_result:
    # Printing $v0
     add     $a0, $v0, $zero     # Load $v0 for printing
    li      $v0, 1              # Load the system call numbers
     syscall
   
    # Print newline.
    la      $a0, newline          # Load value for printing
     li      $v0, 4                # Load the system call numbers
     syscall
   
     #Printing $v1
     add     $a0, $v1, $zero      # Load $v1 for printing
     li      $v0, 1                # Load the system call numbers
     syscall

    # Print newline.
     la      $a0, newline          # Load value for printing
     li      $v0, 4                # Load the system call numbers
     syscall
   
    # Print newline.
     la      $a0, newline          # Load value for printing
     li      $v0, 4                # Load the system call numbers
     syscall
   
     jr      $ra                   #function return
     #  Fall 2024



     #  Fall 2024
#  Team Members:   Giuseppe Pongelupe Giacoia, Carson Keegan, Leo Dickinson 
##  Fall 2024
#  Team Members:   Giuseppe Pongelupe Giacoia, Carson Keegan, Leo Dickinson 
#  % Effort    :   33%, 33%, 33%

vbsme:  
    addi $v0, $zero, 0              # reset $v0 and $V1
    addi $v1, $zero, 0

    # insert your code here
    lw $t0, 0($a0) # t0 = i 
    lw $t1, 4($a0) # t1 = j 
    lw $t2, 8($a0) # t2 = k
    lw $t3, 12($a0) # t3 = l
    
    # v0 and v1 are final indexes x and y

    addi $t8, $zero, 0 # currIndex = 0;
    addi $s6, $zero, 32767 # s6 = "set to a very large value"
    addi $s2, $zero, 0 #TO BE THE FINAL INDEX  1D

    #t5 - t7 are now for calculations, temporary values

    # t5 to be used as right wall, t0 used later too
    sub $s0, $t1, $t3 # right wall
    addi $s0, $s0, 1
    sub $s1, $t0, $t2 # floor
    addi $s1, $s1, 1

    mul $t4, $s0, $s1 #how many total moves there are


    
loop: 
    beq $t4, $zero, loopOut       # If current index is out of bounds get tf out
    j calculate

back:
    #bge $t7, $s6, skip #skips if the found value is not a new minimum
    sub $t5, $s4, $s6      # Calculate $t7 - $s6
    bgez $t5, skip         # If $t7 >= $s6, branch to skip

    add $s6, $zero, $s4 #update SAD 
    add $s2, $zero, $t8 #NEW FINAL INDEX

skip: 
    #end of call to funciton
    addi $t8, $t8, 1
    addi $s0, $s0, -1 #subtract one from right
    blez $s0, updateCoordinates

backToLoop:
    addi $t4, $t4, -1
    j loop

updateCoordinates: 
    sub $s0, $t1, $t3 # re update right wall
    addi $s0, $s0, 1
    sub $t8, $t8, $s0
    add $t8, $t8, $t1
    j backToLoop
    
    
loopOut: 
    # here none of the previous stored values matter with the exception of s2
     #now we have to v0 = x = index % j (number of columns)
     #now we have to v1 = y = index // j 
    move $s4, $s2
    li $s3, 0 #s2 = x, s3 = y
loopx:
    #blt $s2, $s1, exitx
    sub $t5, $s4, $t1      
    bltz $t5, exitx        # Check if 

    sub $s4, $s4, $t1 #calculate temp -=j 
    addi $s3, $s3, 1
    j loopx
exitx:
    move $v1, $s4
    move $v0, $s3
    jr $ra
    
calculate: 
    mul $t6, $t2, $t3 # i = k*l;
    addi $t5, $zero, 0 #index = 0;
    move $s3, $t3 #rightCount = l
    li $s4, 0 #sum = 0;
    li $t7, 0 #temp =0;
    move $t9, $t8 # currIndex = w;
calculateLoop: 
    #bge $t5, $t4, back #if index >= i exit
    sub $s5, $t5, $t6     # Calculate $t5 - $t4
    bgez $s5, back         # If $t5 >= $t4, branch to back
    addi $s5, $zero, 4
    mul $s1, $t5, $s5 # gets correct index as 4 bytes per word
    add $s1, $s1, $a2
    lw $t7, 0($s1) #temp = window[index]
    addi $s5, $zero, 4
    mul $s1, $t9, $s5
    add $s1, $s1, $a1
    lw $s7, 0($s1) #temp2 = grid[currIndex]
    sub $t7, $t7, $s7 #temp = window[index] - grid[currIndex]
    
    # Check if temp is negative
    #bge $t0, $t2, positive#go to greater than 0
    bgez $t7, positive      # If $t0 >= $t2, branch to positive
    addi $s7, $zero, -1
    mul $t7, $t7, $s7

positive:
    add $s4, $s4, $t7 #sum +=temp
    #update currIndex
    addi $t9, $t9, 1 #currIndex +=1;
    addi $s3, $s3, -1 #rightCount -=1;
    
    bne $s3, $zero, notzero # skip if rightCount != 0
    sub $t9, $t9, $t3 #currIndex = currIndex - (l)
    add $t9, $t9, $t1 # currIndex +=j
    move $s3, $t3 #rightCount = l

notzero: 
    addi $t5, $t5, 1 #index +=1
    j calculateLoop
    




    
    
    
   
